import React, { useRef, useEffect } from 'react';
import PopupWithForm from "./PopupWithForm";

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar }) {
    //Реф для прямого доступа к DOM-элементу инпута и его значению
    const avatarRef = useRef('');

    //Очистка поля ввода ссылки после закрытия
    React.useEffect(() => {
        avatarRef.current.value = '';
    }, [isOpen])

    //Обработчик сабмита формы (обновление аватарки)
    function handleSubmit(event) {
        event.preventDefault();

        onUpdateAvatar({
            avatar: avatarRef.current.value
        });
    }

    return (
        <PopupWithForm
            name="edit-avatar"
            title="Обновить аватар"
            isOpen={isOpen}
            onClose={onClose}
            buttonName="Сохранить"
            onSubmit={handleSubmit}>


            <input id="input-avatar" type="url" name="avatar" className="popup__input popup__input_avatar" defaultValue required />
            <span id="input-avatar-error" className="popup__form-input-error" />

        </PopupWithForm>
    )
}

export default EditAvatarPopup;
