function PopupWithForm(props) {

  const { name, title, children, isOpen, onClose } = props;

  return (
    <div className={`popup popup_type_${props.name} ${isOpen ? "popup_visible" : ""}`}
      onClick={(evt) => {
        if (evt.target.classList.contains('popup_visible')) {
          onClose();
        }
      }}>
      <div className="popup__container">
        <button id="closebutton-edit-form" className="popup__close-button" type="button" aria-label="закрыть окно" />
        <form className="popup__form" name={props.name} id={props.name} noValidate>
          <h2 className="popup__title">{title}</h2>
          {children}
        </form>
      </div>
    </div>
  )
}

export default PopupWithForm;
