import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function EditProfilePopup({ isOpen, onClose, onUpdateUser }) {
    //Подписываемся на контекст
    const currentUser = React.useContext(CurrentUserContext);

    //Cтейты имени и описания профиля
    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');

    //После загрузки данных пользователя из API, эти данные будут использоваться в управляемых компонентах (полях)
    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    //Функция для изменения имени через поле ввода
    function handleUserName(event) {
        setName(event.target.value)
    }

    //Функция для изменения описания профиля через поле ввода
    function handleUserDescription(event) {
        setDescription(event.target.value)
    }

    //Обработчик сабмита формы
    function handleSubmit(event) {
        event.preventDefault();

        //Передача значений управляемых компонентов во внешний обработчик
        onUpdateUser({
            name: name,
            about: description
        });
    }

    return (

        <PopupWithForm
            name="edit-form"
            title="Редактировать профиль"
            isOpen={isOpen}
            onClose={onClose}
            buttonName='Сохранить'
            onSubmit={handleSubmit}>
            <input type="text" name="name" className="popup__input popup__input_data_name" id="data_name" minLength={2} maxLength={40} placeholder="Имя" required  onChange={handleUserName} value={name ? name : ''}/>
            <span className="popup__form-input-error" id="data_name-error" />
            <input type="text" name="about" className="popup__input popup__input_data_job" id="data_job" minLength={2} maxLength={200} placeholder="О себе" required  onChange={handleUserDescription} value={description ? description : ''}/>
            <span className="popup__form-input-error" id="data_job-error" />
        </PopupWithForm>
    )
}

export default EditProfilePopup;
