import Avatar from '../images/profile__avatar.jpg';

function Main() {
    return (
        <main>
        <section className="profile">
          <img className="profile__avatar" src="" alt="Аватарка" />
          <button type="button" className="profile__avatar-edit-button" aria-label="кнопка для изменения аватара" onClick={handleEditAvatarClick}/>
          <div className="profile__info">
            <h1 className="profile__name" id="name">Жак-Ив Кусто</h1>
            <button className="profile__edit-button" type="button" aria-label="добавление информации" onClick={handleEditProfileClick}/>
            <p className="profile__position" id="position-job">Исследователь океана</p>
          </div>
          <button className="profile__plus-button" type="button" aria-label="редактирование профиля" onClick={handleAddPlaceClick}/>
        </section>
        <section className="elements" aria-label="Фотографии пользователя">
          <ul className="cards">
            <template id="cardsTemplate" />
          </ul>
        </section>
      </main>
    )
}

export default Main;

function handleEditProfileClick() {
  document.querySelector('.popup_type_edit-form').classList.add('popup_opened');
}


function handleEditAvatarClick() {
  document.querySelector('.popup_type_edit-avatar').classList.add('popup_opened');
}


function handleAddPlaceClick() {
  document.querySelector('.popup_type_add-card').classList.add('popup_opened');
}
