import '../index.css';
import React from 'react';
import { api } from '../utils/Api';

import Footer from './Footer';
import Header from './Header';
import Main from './Main';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";



function App() {

  //Стейт для данных пользователя
  const [currentUser, setCurrentUser] = React.useState({})

  React.useEffect(() => {
    Promise.all([
      api.getUserInfo(),
      api.getInitialCard()
    ])
      .then(([profileInfo, cards]) => {
        setCurrentUser(profileInfo)
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //Стейты для поп-апов (состояние - открыт / не открыт
  const [isEditAvatarPopupOpen, setIsAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  //Стейт для выбранной карточки, исп. в поп-апе картинки в полном размере
  const [selectedCard, setSelectedCard] = React.useState({});

  //Обработчик клика по изображению карточки (открытие поп-апа картинки)
  function handleCardClick(props) {
    setSelectedCard(props);
  }

  //Обработчик для отправки данных пользователя на сервер (редактирование данных профиля)
  function handleUpdateUser(user) {
    api.setUserInfo(user.name, user.about)
      .then(res => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch(err => console.log(err))
  }


  //Обработчик для обновления аватарки пользователя (отправка на сервер через API)
  function handleUpdateAvatar(user) {
    api.addNewAvatar(user.avatar)
      .then(res => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch(err => console.log(err))
  }

//Обработчик кнопки редактирования аватарки
function handleEditAvatarClick() {
  setIsAvatarPopupOpen(true);
}

//Обработчик кнопки редактирования инф-ии профиля
function handleEditProfileClick() {
  setIsProfilePopupOpen(true);
}

//Обработчик кнопки добавления карточки
function handleAddPlaceClick() {
  setIsAddPlacePopupOpen(true);
}

 //Обработчик закрытия поп-апов
 function closeAllPopups() {
  setIsAddPlacePopupOpen(false);
  setIsAvatarPopupOpen(false);
  setIsProfilePopupOpen(false);
  setSelectedCard(null);
}

 //Стейт для карточек
 const [cards, setCards] = React.useState([]);

// //Получаем данные по пользователю и карточки с сервера
// React.useEffect(() => {
//   api.getInitialCard()
//     .then(res => {
//       setCards(res);
//     })
//     .catch((err) => console.log(err));
// }, []);

// //Функция лайка карточки
// function handleCardLike(card) {
//   //Проверяем, есть ли уже лайк на этой карточке
//   const isLiked = card.likes.some(i => i._id === currentUser._id);

//   // Отправляем запрос в API и получаем обновлённые данные карточки
//   api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
//     // Формируем новый массив на основе имеющегося, подставляя в него новую карточку
//     const newCards = cards.map((c) => c._id === card._id ? newCard : c);
//     // Обновляем стейт
//     setCards(newCards);
//   })
//     .catch(err => console.log(err));
// }

function handleCardLike(card) {
  // Снова проверяем, есть ли уже лайк на этой карточке
  const isLiked = card.likes.some(i => i._id === currentUser._id);

  // Отправляем запрос в API и получаем обновлённые данные карточки
  api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
  });
} 

  //Функция удаления карточки, по аналогии с функцией лайка
  function handleCardDelete(card) {
    api.removeCard(card._id)
      .then(() => {
        setCards(cards.filter(item => item._id !== card._id));
      })
      .catch(err => console.log(err));
  }

  //Функция добавления карточки
  function handleAddPlace(card) {
    api.addNewCard(card.name, card.link)
      .then(res => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch(err => console.log(err))
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main
          isEditAvatarPopupOpen={handleEditAvatarClick}
          isEditProfilePopupOpen={handleEditProfileClick}
          isAddPlacePopupOpen={handleAddPlaceClick}
          onCardClick={handleCardClick}
          cards={cards}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
          />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <PopupWithForm
          onClose={closeAllPopups}
          name="confirm-delete"
          title="Вы уверены?"
          buttonName="Да"
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlace}
        />
        <ImagePopup
          card={selectedCard}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
