import React from 'react';
import { api } from '../utils/Api';
import Card from './Card';

function Main(props) {

  const { onEditProfile, onAddPlace, onEditAvatar, onCard } = props;

  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar, setUserAvatar] = React.useState('');
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((info) => {
        setUserName(info.name);
        setUserDescription(info.about);
        setUserAvatar(info.avatar);
      })
      .catch((err) => {
        console.log(err);
      });

    api
      .getCards()
      .then((item) => {
        setCards(...cards, item);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

    return (
        <main>
        <section className="profile">
          <img className="profile__avatar"  src={userAvatar} alt="Аватарка" />
          <button type="button" className="profile__avatar-edit-button" aria-label="кнопка для изменения аватара" onClick={handleEditAvatarClick}/>
          <div className="profile__info">
            <h1 className="profile__name" id="name">{userName}</h1>
            <button className="profile__edit-button" type="button" aria-label="добавление информации" onClick={handleEditProfileClick}/>
            <p className="profile__position" id="position-job">Исследователь океана</p>
          </div>
          <button className="profile__plus-button" type="button" aria-label="редактирование профиля" onClick={handleAddPlaceClick}/>
        </section>
        <section className="elements" aria-label="Фотографии пользователя">
          <ul className="cards">
            <template id="cardsTemplate" />
          </ul>
        </section>
      </main>
    )
}

export default Main;
